import java.io.Serializable;

public class Experiment implements Serializable {
	private int delay;
	private double loss;
	private int throughput;
	
	public Experiment(int delay, double loss, int throughput) {
		setDelay(delay);
		setLoss(loss);
		setThroughput(throughput);
	}
	

	// Methods hashCode() and equals() are generated by the Eclipse for the purpose of using the instances of the 
	// class Experiment as a key of a hash-map
	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + delay;
		long temp;
		temp = Double.doubleToLongBits(loss);
		result = prime * result + (int) (temp ^ (temp >>> 32));
		result = prime * result + throughput;
		return result;
	}


	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Experiment other = (Experiment) obj;
		if (delay != other.delay)
			return false;
		if (Double.doubleToLongBits(loss) != Double.doubleToLongBits(other.loss))
			return false;
		if (throughput != other.throughput)
			return false;
		return true;
	}
	
	// Setter, getter for the data members
	public void setDelay(int delay) {
		this.delay = delay;
	}
	
	public int getDelay() {
		return this.delay;
	}
	
	public void setLoss(double loss) {
		this.loss = loss;
	}
	
	public double getLoss() {
		return this.loss;
	}
	
	public void setThroughput(int throughput) {
		this.throughput = throughput;
	}
	
	public int getThroughput() {
		return this.throughput;
	}

}
